mapper.select:
SELECT * FROM ( SELECT *, (SELECT CODE_NM FROM REF_CODE WHERE CODE_ID = IPCC_LARGE) AS IPCC_LARGE_NM, (SELECT CODE_NM FROM REF_CODE WHERE CODE_ID = IPCC_SMALL) AS IPCC_SMALL_NM FROM INDICATOR_LIST
WHERE INDI_NM LIKE '%' || #{keyword} || '%'
ORDER BY INDI_ID DESC LIMIT #{endRow} ) A OFFSET #{startRow}

SELECT * FROM ( SELECT * FROM REF_META_2022
WHERE META_NM LIKE '%' || #{keyword} || '%'
ORDER BY META_ID DESC LIMIT #{endRow} ) A OFFSET #{startRow}
